INSERT INTO `medicine_tb` VALUES ('3', '蕲蛇', '甘、咸、温；有毒。趋向升浮。归肝经', 'http://47.102.155.48:8080/pic_medicineapp/qishe.jpg', '1.风湿顽痹，中风半身不遂 2.小儿惊风，破伤风 3.麻风，疥廯', '祛风，通络，止痉');
INSERT INTO `medicine_tb` VALUES ('4', '独活', '辛、苦，微温。归肾、膀胱经', 'http://47.102.155.48:8080/pic_medicineapp/duhuo.jpg', '1.风寒湿痹 2.风寒夹湿表证', '祛风除湿，通痹止痛');
INSERT INTO `medicine_tb` VALUES ('5', '郁李仁', '辛、苦、甘，平。趋向沉降。归脾、大肠、小肠经', 'http://47.102.155.48:8080/pic_medicineapp/yuliren.jpg', '1.肠燥便秘 2.水肿腹满，脚气浮肿', '润肠通便，下气利水');
INSERT INTO `medicine_tb` VALUES ('6', '漏芦', '苦，寒。归胃经', 'http://47.102.155.48:8080/pic_medicineapp/loulu.jpg', '1.热毒疮痈，乳痈 2.产后乳房胀痛，乳汁不下 3.湿痹拘挛', '清热解毒，消痈，下乳，舒经通脉');
INSERT INTO `medicine_tb` VALUES ('7', '黄连', '苦、寒。趋向沉降。归心，脾，胃，肝、胆、大肠经', 'http://47.102.155.48:8080/pic_medicineapp/huanglian.jpg', '1.湿热痞满，泻痢，黄疸 2.高热神昏 3.心火亢盛，心烦不寐 4.胃热呕吐，吞酸，消渴', '清热湿燥，泻火解毒');
